package com.Sudarshan;

public class Nestedforloop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		int j;
		for(i=5;i>=1;i--)
		{
			
	 	 for(j=1;j<=1;j++)
		
		{
			System.out.print("*");
		}
		
			System.out.println();
		}
		

	}

}
